package com.zhengdianfang.miracledemo


import android.os.Bundle
import android.support.v4.app.Fragment
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.Toast
import com.zhengdianfang.miracleframework.BaseFragment
import com.zhengdianfang.miracleframework.adapter.base.BaseMultiItemQuickAdapter
import com.zhengdianfang.miracleframework.adapter.base.BaseViewHolder
import com.zhengdianfang.miracleframework.adapter.base.entity.MultiItemEntity
import kotlinx.android.synthetic.main.fragment_list.*

/**
 * A simple [Fragment] subclass.
 *
 */
class MultiListFragment : BaseFragment() {
    companion object {
        fun newInstance() = MultiListFragment()
    }

    override fun onCreateView(inflater: LayoutInflater, container: ViewGroup?,
                              savedInstanceState: Bundle?): View? {
        // Inflate the layout for this fragment
        return inflater.inflate(R.layout.fragment_list, container, false)
    }

    override fun onActivityCreated(savedInstanceState: Bundle?) {
        super.onActivityCreated(savedInstanceState)
        val list = arrayListOf<MultipleItem>()
        for(index in 1..50) {
            if (index % 2 == 0) {
                list.add(MultipleItem(MultipleItem.TEXT))
            } else {

                list.add(MultipleItem(MultipleItem.IMG))
            }
        }
        val listAdapter = ListAdapter(list)
        listAdapter.setEnableLoadMore(true)
        listAdapter.isUpFetchEnable = true
        listAdapter.setUpFetchListener {
            Toast.makeText(context, "refresh", Toast.LENGTH_SHORT).show();
        }
        listAdapter.setRequestLoadMoreListener({
            Toast.makeText(context, "load more", Toast.LENGTH_SHORT).show();
        }, recyclerView)
        recyclerView.adapter = listAdapter

    }

    internal class ListAdapter(data: MutableList<MultipleItem>) : BaseMultiItemQuickAdapter<MultipleItem, BaseViewHolder>(data) {
        init {
            addItemType(MultipleItem.IMG, R.layout.multi_image_item)
            addItemType(MultipleItem.TEXT, R.layout.multi_text_item)
        }
        override fun convert(helper: BaseViewHolder?, item: MultipleItem?) {
            when(helper?.itemViewType) {
                MultipleItem.IMG -> {
                   helper?.setImageUrl(R.id.image, "data:image/jpeg;base64,")
                }
                MultipleItem.TEXT -> {
                   helper?.setText(R.id.text, "https://cdn2.jianshu.io/assets/web/nav-logo-4c7bbafe27adc892f3046e6978459bac.png")
                }
            }
        }

    }

    internal class MultipleItem(private var itemType: Int) : MultiItemEntity {
        companion object {
            val TEXT = 1
            val IMG = 2
        }

        override fun getItemType(): Int {
           return this.itemType
        }

    }
}
